import React, { useState, useEffect } from "react";
import {
  Plus,
  User,
  Users,
  Bluetooth,
  Download,
  Search,
  Settings,
  ArrowLeft,
  Calendar,
} from "lucide-react";

const TriageApp = () => {
  const [currentScreen, setCurrentScreen] = useState("main");
  const [searchTerm, setSearchTerm] = useState("");
  const [filterPriority, setFilterPriority] = useState("all");
  const [filterSex, setFilterSex] = useState("all");
  const [patients, setPatients] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);

  // Load data on initialization
  useEffect(() => {
    try {
      const savedPatients = localStorage.getItem("triagePatients");
      if (savedPatients) {
        setPatients(JSON.parse(savedPatients));
      } else {
        // Example data if nothing is saved
        const examplePatients = [
          {
            id: 1,
            name: "Michael",
            age: 50,
            time: "09:30",
            date: "2025-07-19",
            priority: "stable",
            symptoms: "Mild arm pain",
            sex: "male",
            triageAnswers: {
              breathing: true,
              conscious: true,
              walking: true,
              bleeding: false,
            },
          },
          {
            id: 2,
            name: "Anna",
            age: 34,
            time: "09:15",
            date: "2025-07-19",
            priority: "urgent",
            symptoms: "Difficulty breathing",
            sex: "female",
            triageAnswers: {
              breathing: true,
              conscious: true,
              walking: false,
              bleeding: false,
            },
          },
          {
            id: 3,
            name: "Laura",
            age: 72,
            time: "09:00",
            date: "2025-07-19",
            priority: "critical",
            symptoms: "Severe chest pain",
            sex: "female",
            triageAnswers: {
              breathing: false,
              conscious: true,
              walking: false,
              bleeding: false,
            },
          },
        ];
        setPatients(examplePatients);
      }
    } catch (error) {
      console.error("Error loading patients:", error);
    }
    setIsLoaded(true);
  }, []);

  // Save data whenever patients change (but not on first render)
  useEffect(() => {
    if (isLoaded) {
      try {
        localStorage.setItem("triagePatients", JSON.stringify(patients));
      } catch (error) {
        console.error("Error saving patients:", error);
      }
    }
  }, [patients, isLoaded]);
  const [newPatient, setNewPatient] = useState({
    name: "",
    age: "",
    sex: "male",
    symptoms: "",
  });
  const [triageAnswers, setTriageAnswers] = useState({
    breathing: null,
    pulse: null,
    conscious: null,
    walking: null,
    bleeding: null,
  });

  const sexLabels = {
    male: "Male",
    female: "Female",
  };

  // Filter patients
  const filteredPatients = patients.filter((patient) => {
    const matchesSearch =
      patient.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      patient.symptoms.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesPriority =
      filterPriority === "all" || patient.priority === filterPriority;
    const matchesSex = filterSex === "all" || patient.sex === filterSex;

    return matchesSearch && matchesPriority && matchesSex;
  });
  // Export functions
  const exportToJSON = () => {
    const dataStr = JSON.stringify(patients, null, 2);
    const dataBlob = new Blob([dataStr], { type: "application/json" });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const exportToCSV = () => {
    const headers = [
      "ID",
      "Name",
      "Age",
      "Sex",
      "Date",
      "Time",
      "Priority",
      "Symptoms",
      "Breathing",
      "Conscious",
      "Walking",
      "Bleeding",
    ];
    const csvContent = [
      headers.join(","),
      ...patients.map((patient) =>
        [
          patient.id,
          `"${patient.name}"`,
          patient.age,
          sexLabels[patient.sex],
          patient.date,
          patient.time,
          patient.priority,
          `"${patient.symptoms}"`,
          patient.triageAnswers?.breathing ? "Yes" : "No",
          patient.triageAnswers?.conscious ? "Yes" : "No",
          patient.triageAnswers?.walking ? "Yes" : "No",
          patient.triageAnswers?.bleeding ? "Yes" : "No",
        ].join(",")
      ),
    ].join("\n");

    const dataBlob = new Blob([csvContent], {
      type: "text/csv;charset=utf-8;",
    });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const exportToTXT = () => {
    const txtContent = patients
      .map(
        (patient) =>
          `PATIENT #${patient.id}
Name: ${patient.name}
Age: ${patient.age}
Sex: ${sexLabels[patient.sex]}
Date: ${patient.date}
Time: ${patient.time}
Priority: ${priorityLabels[patient.priority]}
Symptoms: ${patient.symptoms}
Triage assessment:
- Breathing: ${patient.triageAnswers?.breathing ? "Yes" : "No"}
- Conscious: ${patient.triageAnswers?.conscious ? "Yes" : "No"}
- Can walk: ${patient.triageAnswers?.walking ? "Yes" : "No"}
- Severe bleeding: ${patient.triageAnswers?.bleeding ? "Yes" : "No"}

-------------------
`
      )
      .join("\n");

    const dataBlob = new Blob([txtContent], {
      type: "text/plain;charset=utf-8;",
    });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.txt`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const clearAllData = () => {
    if (
      window.confirm(
        "Are you sure you want to delete all patients? This action cannot be undone."
      )
    ) {
      setPatients([]);
      localStorage.removeItem("triagePatients");
    }
  };

  const priorityColors = {
    critical: "bg-red-500",
    urgent: "bg-yellow-500",
    stable: "bg-green-500",
  };

  const priorityLabels = {
    critical: "Critical",
    urgent: "Urgent",
    stable: "Stable",
  };

  const calculatePriority = () => {
    const { breathing, conscious, bleeding, walking } = triageAnswers;

    if (breathing === false || conscious === false || bleeding === true) {
      return "critical";
    }
    if (walking === false) {
      return "urgent";
    }
    return "stable";
  };

  const handleSavePatient = () => {
    const priority = calculatePriority();
    const now = new Date();
    const time = now.toLocaleTimeString("en-US", {
      hour: "2-digit",
      minute: "2-digit",
    });
    const date = now.toISOString().split("T")[0];

    const patient = {
      id: Date.now(),
      ...newPatient,
      time,
      date,
      priority,
      triageAnswers: { ...triageAnswers },
    };

    setPatients((prev) => [patient, ...prev]);
    setNewPatient({ name: "", age: "", sex: "male", symptoms: "" });
    setTriageAnswers({
      breathing: null,
      pulse: null,
      conscious: null,
      walking: null,
      bleeding: null,
    });
    setCurrentScreen("patients");
  };

  return (
    <div className="font-sans">
      {currentScreen === "main" && (
        <MainScreen setCurrentScreen={setCurrentScreen} />
      )}
      {currentScreen === "register" && (
        <RegisterScreen
          setCurrentScreen={setCurrentScreen}
          newPatient={newPatient}
          setNewPatient={setNewPatient}
        />
      )}
      {currentScreen === "triage" && (
        <TriageScreen
          setCurrentScreen={setCurrentScreen}
          triageAnswers={triageAnswers}
          setTriageAnswers={setTriageAnswers}
          priorityColors={priorityColors}
          priorityLabels={priorityLabels}
          calculatePriority={calculatePriority}
          handleSavePatient={handleSavePatient}
        />
      )}
      {currentScreen === "patients" && (
        <PatientsScreen
          setCurrentScreen={setCurrentScreen}
          patients={patients}
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          filterPriority={filterPriority}
          setFilterPriority={setFilterPriority}
          filterSex={filterSex}
          setFilterSex={setFilterSex}
          filteredPatients={filteredPatients}
          exportToJSON={exportToJSON}
          exportToCSV={exportToCSV}
          exportToTXT={exportToTXT}
          clearAllData={clearAllData}
          priorityColors={priorityColors}
          priorityLabels={priorityLabels}
          sexLabels={sexLabels}
        />
      )}
    </div>
  );
};

