import React, { useState, useEffect } from "react";
import {
  Plus,
  User,
  Users,
  FileText,
  Bluetooth,
  Download,
  Search,
  Settings,
  ArrowLeft,
  Calendar,
} from "lucide-react";

const TriageApp = () => {
  const [currentScreen, setCurrentScreen] = useState("main");
  const [patients, setPatients] = useState([
    {
      id: 1,
      name: "Miguel",
      age: 50,
      time: "09:30",
      date: "2025-07-19",
      priority: "stable",
      symptoms: "Dolor leve en brazo",
      sex: "masculino",
      triageAnswers: {
        breathing: true,
        conscious: true,
        walking: true,
        bleeding: false,
      },
    },
    {
      id: 2,
      name: "Anna",
      age: 34,
      time: "09:15",
      date: "2025-07-19",
      priority: "urgent",
      symptoms: "Dificultad respiratoria",
      sex: "femenino",
      triageAnswers: {
        breathing: true,
        conscious: true,
        walking: false,
        bleeding: false,
      },
    },
    {
      id: 3,
      name: "Laura",
      age: 72,
      time: "09:00",
      date: "2025-07-19",
      priority: "critical",
      symptoms: "Dolor en pecho severo",
      sex: "femenino",
      triageAnswers: {
        breathing: false,
        conscious: true,
        walking: false,
        bleeding: false,
      },
    },
  ]);

  // Para usar en tu propio entorno, descomenta estas líneas:

  useEffect(() => {
    const savedPatients = localStorage.getItem("triagePatients");
    if (savedPatients) {
      setPatients(JSON.parse(savedPatients));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("triagePatients", JSON.stringify(patients));
  }, [patients]);
  const [newPatient, setNewPatient] = useState({
    name: "",
    age: "",
    sex: "masculino",
    symptoms: "",
  });
  const [triageAnswers, setTriageAnswers] = useState({
    breathing: null,
    pulse: null,
    conscious: null,
    walking: null,
    bleeding: null,
  });

  // Funciones de exportación
  const exportToJSON = () => {
    const dataStr = JSON.stringify(patients, null, 2);
    const dataBlob = new Blob([dataStr], { type: "application/json" });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const exportToCSV = () => {
    const headers = [
      "ID",
      "Nombre",
      "Edad",
      "Sexo",
      "Fecha",
      "Hora",
      "Prioridad",
      "Síntomas",
      "Respirando",
      "Consciente",
      "Caminando",
      "Sangrado",
    ];
    const csvContent = [
      headers.join(","),
      ...patients.map((patient) =>
        [
          patient.id,
          `"${patient.name}"`,
          patient.age,
          patient.sex,
          patient.date,
          patient.time,
          patient.priority,
          `"${patient.symptoms}"`,
          patient.triageAnswers?.breathing ? "Sí" : "No",
          patient.triageAnswers?.conscious ? "Sí" : "No",
          patient.triageAnswers?.walking ? "Sí" : "No",
          patient.triageAnswers?.bleeding ? "Sí" : "No",
        ].join(",")
      ),
    ].join("\n");

    const dataBlob = new Blob([csvContent], {
      type: "text/csv;charset=utf-8;",
    });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const exportToTXT = () => {
    const txtContent = patients
      .map(
        (patient) =>
          `PACIENTE #${patient.id}
Nombre: ${patient.name}
Edad: ${patient.age}
Sexo: ${patient.sex}
Fecha: ${patient.date}
Hora: ${patient.time}
Prioridad: ${priorityLabels[patient.priority]}
Síntomas: ${patient.symptoms}
Evaluación de triaje:
- Respirando: ${patient.triageAnswers?.breathing ? "Sí" : "No"}
- Consciente: ${patient.triageAnswers?.conscious ? "Sí" : "No"}
- Puede caminar: ${patient.triageAnswers?.walking ? "Sí" : "No"}
- Sangrado grave: ${patient.triageAnswers?.bleeding ? "Sí" : "No"}

-------------------
`
      )
      .join("\n");

    const dataBlob = new Blob([txtContent], {
      type: "text/plain;charset=utf-8;",
    });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `triaje-pacientes-${
      new Date().toISOString().split("T")[0]
    }.txt`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const clearAllData = () => {
    if (
      window.confirm(
        "¿Estás seguro de que quieres eliminar todos los pacientes? Esta acción no se puede deshacer."
      )
    ) {
      setPatients([]);
      // Para tu propio entorno, descomenta esta línea:
      // localStorage.removeItem('triagePatients');
    }
  };

  const priorityColors = {
    critical: "bg-red-500",
    urgent: "bg-yellow-500",
    stable: "bg-green-500",
  };

  const priorityLabels = {
    critical: "Crítico",
    urgent: "Urgente",
    stable: "Estable",
  };

  const calculatePriority = () => {
    const { breathing, conscious, bleeding, walking } = triageAnswers;

    if (breathing === false || conscious === false || bleeding === true) {
      return "critical";
    }
    if (walking === false) {
      return "urgent";
    }
    return "stable";
  };

  const handleSavePatient = () => {
    const priority = calculatePriority();
    const now = new Date();
    const time = now.toLocaleTimeString("es-ES", {
      hour: "2-digit",
      minute: "2-digit",
    });
    const date = now.toISOString().split("T")[0];

    const patient = {
      id: Date.now(),
      ...newPatient,
      time,
      date,
      priority,
      triageAnswers: { ...triageAnswers },
    };

    setPatients((prev) => [patient, ...prev]);
    setNewPatient({ name: "", age: "", sex: "masculino", symptoms: "" });
    setTriageAnswers({
      breathing: null,
      pulse: null,
      conscious: null,
      walking: null,
      bleeding: null,
    });
    setCurrentScreen("patients");
  };

  const MainScreen = () => (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-md mx-auto">
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-red-500 rounded-full mx-auto mb-4 flex items-center justify-center">
            <div className="w-8 h-8 bg-white rounded-sm flex items-center justify-center">
              <div className="w-4 h-4 bg-red-500 rounded-full"></div>
            </div>
          </div>
          <h1 className="text-2xl font-bold text-gray-800 mb-2">FirstHelp</h1>
          <p className="text-gray-600">Triaje médico de emergencia</p>
        </div>

        <div className="space-y-4">
          <button
            onClick={() => setCurrentScreen("register")}
            className="w-full bg-blue-500 text-white p-4 rounded-lg flex items-center justify-between hover:bg-blue-600 transition-colors"
          >
            <div className="flex items-center">
              <User className="w-6 h-6 mr-3" />
              <span className="text-lg font-medium">Nuevo paciente</span>
            </div>
          </button>

          <button
            onClick={() => setCurrentScreen("patients")}
            className="w-full bg-teal-500 text-white p-4 rounded-lg flex items-center justify-between hover:bg-teal-600 transition-colors"
          >
            <div className="flex items-center">
              <Users className="w-6 h-6 mr-3" />
              <span className="text-lg font-medium">Ver pacientes</span>
            </div>
          </button>
        </div>

        <div className="mt-8 flex justify-center space-x-4">
          <button className="p-3 bg-white rounded-lg shadow-sm">
            <Settings className="w-6 h-6 text-gray-600" />
          </button>
          <button className="p-3 bg-white rounded-lg shadow-sm">
            <Search className="w-6 h-6 text-gray-600" />
          </button>
        </div>
      </div>
    </div>
  );

  const RegisterScreen = () => (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-md mx-auto">
        <div className="flex items-center mb-6">
          <button onClick={() => setCurrentScreen("main")} className="mr-4">
            <ArrowLeft className="w-6 h-6 text-gray-600" />
          </button>
          <h2 className="text-xl font-bold text-gray-800">Nuevo paciente</h2>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Nombre
            </label>
            <input
              type="text"
              value={newPatient.name}
              onChange={(e) =>
                setNewPatient((prev) => ({ ...prev, name: e.target.value }))
              }
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Nombre del paciente"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Edad
            </label>
            <input
              type="number"
              value={newPatient.age}
              onChange={(e) =>
                setNewPatient((prev) => ({ ...prev, age: e.target.value }))
              }
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Edad"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Sexo
            </label>
            <select
              value={newPatient.sex}
              onChange={(e) =>
                setNewPatient((prev) => ({ ...prev, sex: e.target.value }))
              }
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="masculino">Masculino</option>
              <option value="femenino">Femenino</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Síntomas principales
            </label>
            <textarea
              value={newPatient.symptoms}
              onChange={(e) =>
                setNewPatient((prev) => ({ ...prev, symptoms: e.target.value }))
              }
              className="w-full p-3 border border-gray-300 rounded-lg h-20 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Descripción de síntomas"
            />
          </div>

          <button
            onClick={() => setCurrentScreen("triage")}
            disabled={!newPatient.name || !newPatient.age}
            className="w-full bg-blue-500 text-white p-4 rounded-lg font-medium hover:bg-blue-600 transition-colors disabled:bg-gray-300 disabled:cursor-not-allowed"
          >
            Continuar al triaje
          </button>
        </div>
      </div>
    </div>
  );

  const TriageScreen = () => {
    const questions = [
      { key: "breathing", question: "¿Está respirando?", critical: true },
      { key: "conscious", question: "¿Está consciente?", critical: true },
      { key: "walking", question: "¿Puede caminar?", critical: false },
      {
        key: "bleeding",
        question:
          "¿Tiene heridas graves, fracturas abiertas o sangrado activo?",
        critical: true,
        inverted: true,
      },
    ];

    const currentQuestionIndex = questions.findIndex(
      (q) => triageAnswers[q.key] === null
    );
    const isComplete = currentQuestionIndex === -1;

    if (isComplete) {
      const priority = calculatePriority();
      return (
        <div className="min-h-screen bg-gray-50 p-4">
          <div className="max-w-md mx-auto">
            <div className="flex items-center mb-6">
              <button
                onClick={() => setCurrentScreen("register")}
                className="mr-4"
              >
                <ArrowLeft className="w-6 h-6 text-gray-600" />
              </button>
              <h2 className="text-xl font-bold text-gray-800">
                Resultado del triaje
              </h2>
            </div>

            <div className="bg-white p-6 rounded-lg shadow-sm mb-6">
              <div className="text-center">
                <div
                  className={`w-20 h-20 ${priorityColors[priority]} rounded-full mx-auto mb-4 flex items-center justify-center`}
                >
                  <span className="text-white font-bold text-xl">
                    {priority[0].toUpperCase()}
                  </span>
                </div>
                <h3 className="text-2xl font-bold text-gray-800 mb-2">
                  {priorityLabels[priority]}
                </h3>
                <p className="text-gray-600">
                  {priority === "critical" && "Atención inmediata requerida"}
                  {priority === "urgent" && "Atención prioritaria"}
                  {priority === "stable" &&
                    "Puede esperar, atención no urgente"}
                </p>
              </div>
            </div>

            <div className="space-y-3">
              <button
                onClick={handleSavePatient}
                className="w-full bg-green-500 text-white p-4 rounded-lg font-medium hover:bg-green-600 transition-colors"
              >
                Guardar paciente
              </button>
              <button
                onClick={() => setCurrentScreen("register")}
                className="w-full bg-gray-300 text-gray-700 p-4 rounded-lg font-medium hover:bg-gray-400 transition-colors"
              >
                Modificar datos
              </button>
            </div>
          </div>
        </div>
      );
    }

    const currentQuestion = questions[currentQuestionIndex];

    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="max-w-md mx-auto">
          <div className="flex items-center mb-6">
            <button
              onClick={() => setCurrentScreen("register")}
              className="mr-4"
            >
              <ArrowLeft className="w-6 h-6 text-gray-600" />
            </button>
            <h2 className="text-xl font-bold text-gray-800">Triaje</h2>
          </div>

          <div className="mb-4">
            <div className="flex space-x-2 mb-4">
              {questions.map((_, index) => (
                <div
                  key={index}
                  className={`h-2 flex-1 rounded-full ${
                    index < currentQuestionIndex
                      ? "bg-blue-500"
                      : index === currentQuestionIndex
                      ? "bg-blue-300"
                      : "bg-gray-200"
                  }`}
                />
              ))}
            </div>
            <p className="text-sm text-gray-600">
              Pregunta {currentQuestionIndex + 1} de {questions.length}
            </p>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-sm mb-6">
            <h3 className="text-lg font-medium text-gray-800 mb-6 text-center">
              {currentQuestion.question}
            </h3>

            <div className="space-y-3">
              <button
                onClick={() =>
                  setTriageAnswers((prev) => ({
                    ...prev,
                    [currentQuestion.key]: true,
                  }))
                }
                className="w-full p-4 bg-green-500 text-white rounded-lg font-medium hover:bg-green-600 transition-colors"
              >
                Sí
              </button>
              <button
                onClick={() =>
                  setTriageAnswers((prev) => ({
                    ...prev,
                    [currentQuestion.key]: false,
                  }))
                }
                className="w-full p-4 bg-red-500 text-white rounded-lg font-medium hover:bg-red-600 transition-colors"
              >
                No
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const PatientsScreen = () => (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-md mx-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center">
            <button onClick={() => setCurrentScreen("main")} className="mr-4">
              <ArrowLeft className="w-6 h-6 text-gray-600" />
            </button>
            <h2 className="text-xl font-bold text-gray-800">Pacientes</h2>
          </div>
          <button onClick={() => setCurrentScreen("register")}>
            <Plus className="w-6 h-6 text-blue-500" />
          </button>
        </div>

        <div className="mb-4">
          <div className="relative">
            <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
            <input
              type="text"
              placeholder="Buscar pacientes"
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
        </div>

        <div className="space-y-3 mb-6">
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h3 className="font-medium text-gray-800 mb-3 flex items-center">
              <Download className="w-5 h-5 mr-2" />
              Exportar datos
            </h3>
            <div className="grid grid-cols-3 gap-2">
              <button
                onClick={exportToJSON}
                className="p-2 bg-blue-500 text-white rounded text-sm font-medium hover:bg-blue-600 transition-colors"
              >
                JSON
              </button>
              <button
                onClick={exportToCSV}
                className="p-2 bg-green-500 text-white rounded text-sm font-medium hover:bg-green-600 transition-colors"
              >
                CSV
              </button>
              <button
                onClick={exportToTXT}
                className="p-2 bg-purple-500 text-white rounded text-sm font-medium hover:bg-purple-600 transition-colors"
              >
                TXT
              </button>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h3 className="font-medium text-gray-800 mb-3">Gestión de datos</h3>
            <div className="space-y-2">
              <button className="w-full p-2 bg-gray-100 text-gray-700 rounded text-sm font-medium hover:bg-gray-200 transition-colors flex items-center justify-center">
                <Bluetooth className="w-4 h-4 mr-2" />
                Compartir por Bluetooth
              </button>
              <button
                onClick={clearAllData}
                className="w-full p-2 bg-red-100 text-red-700 rounded text-sm font-medium hover:bg-red-200 transition-colors"
              >
                Limpiar todos los datos
              </button>
            </div>
          </div>

          <div className="bg-blue-50 p-4 rounded-lg">
            <p className="text-sm text-blue-800 mb-2">
              <strong>Total de pacientes:</strong> {patients.length}
            </p>
            <div className="flex space-x-4 text-xs">
              <span className="text-red-600">
                Críticos:{" "}
                {patients.filter((p) => p.priority === "critical").length}
              </span>
              <span className="text-yellow-600">
                Urgentes:{" "}
                {patients.filter((p) => p.priority === "urgent").length}
              </span>
              <span className="text-green-600">
                Estables:{" "}
                {patients.filter((p) => p.priority === "stable").length}
              </span>
            </div>
          </div>
        </div>

        <div className="space-y-3">
          {patients.map((patient) => (
            <div key={patient.id} className="bg-white p-4 rounded-lg shadow-sm">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center mb-1">
                    <h3 className="font-medium text-gray-800 mr-2">
                      {patient.name}
                    </h3>
                    <span className="text-sm text-gray-500">
                      {patient.age} años
                    </span>
                  </div>
                  <p className="text-sm text-gray-600 mb-2">
                    {patient.symptoms}
                  </p>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Calendar className="w-4 h-4 text-gray-400" />
                      <span className="text-sm text-gray-500">
                        {patient.date} {patient.time}
                      </span>
                    </div>
                    <span
                      className={`px-3 py-1 rounded-full text-xs font-medium text-white ${
                        priorityColors[patient.priority]
                      }`}
                    >
                      {priorityLabels[patient.priority]}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  return (
    <div className="font-sans">
      {currentScreen === "main" && <MainScreen />}
      {currentScreen === "register" && <RegisterScreen />}
      {currentScreen === "triage" && <TriageScreen />}
      {currentScreen === "patients" && <PatientsScreen />}
    </div>
  );
};

export default TriageApp;
